layui.define(["layer"],function(i){var e=layui.layer;i("popup",new function(){this.success=function(i){e.msg(i,{icon:1,time:1e3})},this.failure=function(i){e.msg(i,{icon:2,time:1e3})},this.warning=function(i){e.msg(i,{icon:3,time:1e3})},this.success=function(i,n){e.msg(i,{icon:1,time:1e3},n)},this.failure=function(i,n){e.msg(i,{icon:2,time:1e3},n)},this.warming=function(i,n){e.msg(i,{icon:3,time:1e3},n)}})});